void updateButtons(bool reset) {
	if (!reset) {
		if (Key_IsDown(0, L1)) {
			pMenu->L1 = true;
			sleep(150);
		}
		if (Key_IsDown(0, R1)) {
			pMenu->R1 = true;
			sleep(150);
		}
		if (Key_IsDown(0, L2)) {
			pMenu->L2 = true;
			sleep(150);
		}
		if (Key_IsDown(0, R2)) {
			pMenu->R2 = true;
			sleep(150);
		}
		if (Key_IsDown(0, Dpad_Left)) {
			pMenu->Left = true;
			sleep(150);
		}
		if (Key_IsDown(0, Dpad_Right)) {
			pMenu->Right = true;
			sleep(150);
		}
		if (Key_IsDown(0, Square)) {
			pMenu->Square = true;
			sleep(150);
		}
		if (Key_IsDown(0, R3)) {
			pMenu->R3 = true;
			sleep(150);
		}
		if (Key_IsDown(0, L3)) {
			pMenu->L3 = true;
			sleep(150);
		}
	}
	else
		pMenu->L1 = pMenu->R1 = pMenu->L2 = pMenu->R2 = pMenu->Left = pMenu->Right = pMenu->L3 = pMenu->R3 = pMenu->Square = false;
}